import React from "react";
import axios from "axios";

class QuotesComponent extends React.Component {
    constructor() {
        super();
        this.state = {
            name: [],
            quote: [],
            id: 1
        };
    }

    componentDidMount() {
        this.getQuotes();
    }

    getQuotes() {
        axios.get(
            "https://philosophyapi.herokuapp.com/api/ideas/"
        )
            .then(res => {
                let entry = res.data.results.forEach(element => {
                    this.setState({ name: [...element.author], quote: [...element.quote] });
                });
            })
            .catch(e => {
                throw e.res
            });
    }

    render() {
        return (
            <div className="relative w-full gap-4 snap-x snap-mandatory overflow-x-auto p-20 grid grid-cols-4" >
                <div className="snap-center shrink-0">
                    <img className="shrink-0 w-80 h-80 rounded-lg shadow-xl bg-white" />
                    <h3 className="text-lg text-left text-gray-700 pl-5 pt-5">
                        {this.state.name}
                    </h3>
                    <div>
                        <h2 className="text-sm text-left text-gray-400 pl-5 pb-5 ">
                            {this.state.quote}
                        </h2>
                    </div>
                    <div className="snap-center shrink-0">
                        <img className="shrink-0 w-80 h-80 rounded-lg shadow-xl bg-white" />
                        <h3 className="text-lg text-left text-gray-700 pl-5 pt-5">
                            {this.state.name}
                        </h3>
                        <h2 className="text-sm text-left text-gray-400 pl-5 pb-5 ">
                            {this.state.quote}
                        </h2>
                    </div>
                </div>
                <div className="snap-center shrink-0">
                    <img className="shrink-0 w-80 h-80 rounded-lg shadow-xl bg-white" />
                    <h3 className="text-lg text-left text-gray-700 pl-5 pt-5">
                        {this.state.name}
                    </h3>
                    <h2 className="text-sm text-left text-gray-400 pl-5 pb-5 ">
                        {this.state.quote}
                    </h2>
                </div>
                <div className="snap-center shrink-0">
                    <img className="shrink-0 w-80 h-80 rounded-lg shadow-xl bg-white" />
                    <h3 className="text-lg text-left text-gray-700 pl-5 pt-5">
                        {this.state.name}
                    </h3>
                    <div>
                        <h2 className="text-sm text-left text-gray-400 pl-5 pb-5 ">
                            {this.state.quote}
                        </h2>
                    </div>
                </div>
            </div>

        );
    }
}

export default QuotesComponent;
