import React from "react";
import axios from "axios";
import NavBar from "./NavBar";

class QuotesComponent extends React.Component {
    constructor() {
        super();
        this.state = {
            name: [],
            quote: [],
            id: []
        };
    }

    componentDidMount() {
        this.getQuotes();
    }
    getQuotes() {
        axios.get(
            "https://philosophyapi.herokuapp.com/api/"
        )
            .then(res => {
                console.log(res.data)
                let entry = res.data.results.forEach(element => {
                    this.setState({ name: [element.author], quote: [element.quote] });
                });
            })
            .catch(e => {
                throw e.res
            });
    }

    render() {
        return (
            <div>
                {<NavBar/>}
            <div className="flex flex-nowrap relative w-full gap-4 snap-x overflow-x p-20" >
                <div className="snap-center shrink-0">
                    <img className="shrink-0 w-80 h-80 rounded-lg shadow-xl bg-white" />
                    <h3 className="text-lg text-left text-gray-700 pl-5 pt-5">
                        {this.state.name}
                    </h3>
                    <div>
                        <h2 className="text-sm text-left text-gray-400 pl-5 pb-5 ">
                            {this.state.quote}
                        </h2>
                    </div>
                    <div className="snap-center shrink-0">
                        <img className="shrink-0 w-80 h-80 rounded-lg shadow-xl bg-white" />
                        <h3 className="text-lg text-left text-gray-700 pl-5 pt-5">
                            {this.state.name}
                        </h3>
                        <h2 className="text-sm text-left text-gray-400 pl-5 pb-5 ">
                            {this.state.quote}
                        </h2>
                    </div>
                </div>
                <div className="snap-center shrink-0">
                    <img className="shrink-0 w-80 h-80 rounded-lg shadow-xl bg-white" />
                    <h3 className="text-lg text-left text-gray-700 pl-5 pt-5">
                        {this.state.name}
                    </h3>
                    <h2 className="text-sm text-left text-gray-400 pl-5 pb-5 ">
                        {this.state.quote}
                    </h2>
                </div>
                <div className="snap-center shrink-0">
                    <img className="shrink-0 w-80 h-80 rounded-lg shadow-xl bg-white" />
                    <h3 className="text-lg text-left text-gray-700 pl-5 pt-5">
                        {this.state.name}
                    </h3>
                    <div>
                        <h2 className="text-sm text-left text-gray-400 pl-5 pb-5 ">
                            {this.state.quote}
                        </h2>
                    </div>
                </div>
            </div>
            </div>
        );
    }
}

export default QuotesComponent;
