import React from "react";
import axios from "axios";

class QuotesComponent extends React.Component{
    constructor(){
        super();
        this.state = {
            name: [],
            quote: [],
            id: 1
        };
    }

    componentDidMount(){
        this.getQuotes();
    }

    getQuotes(){
        axios.get(
            "https://philosophyapi.herokuapp.com/api/ideas/"
        )
    .then(res => {

        let entry = res.data.results.forEach(element => {
            this.setState({name: element.author, quote: element.quote});   
        });
    })
    .catch(e =>{
        console.log(e.res)
    });
    }

    render(){
        return( 
            <><div className="flex container mx-auto h-screen items-center ">
                <div className="bg-white font-semibold text-center rounded-3xl border shadow-lg max-w-xs w-1/2 justify-center">
                    <img src="https://images.unsplash.com/photo-1604999565976-8913ad2ddb7c?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=320&h=160&q=80"></img>
                    <p className="text-lg text-left text-gray-700 pl-5 pt-5">
                        {this.state.name}
                    </p>
                    <p className="text-sm text-left text-gray-400 pl-5 pb-5">
                        {this.state.quote}
                    </p>
                </div>
            </div><div className="flex mx-auto h-screen items-center ">
                    <div className="bg-white font-semibold text-center rounded-3xl border shadow-lg max-w-xs w-1/2 justify-center">
                        <img src="https://images.unsplash.com/photo-1604999565976-8913ad2ddb7c?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=320&h=160&q=80"></img>
                        <p className="text-lg text-left text-gray-700 pl-5 pt-5">
                            {this.state.name}
                        </p>
                        <p className="text-sm text-left text-gray-400 pl-5 pb-5">
                            {this.state.quote}
                        </p>
                    </div>
                </div></>


        ); 
    }
}

export default QuotesComponent;
