import React from "react";
import axios from "axios";


class QuotesComponent extends React.Component{
    constructor(){
        super();
        this.state = {
            name: [],
            qoute: [],
            id: 1
        };
    }

    componentDidMount(){
        this.getQuotes(this.state.id);
    }

    getQuotes(id){
        axios.get(
            `http https://philosophyapi.herokuapp.com/api/philosophers/${id}`
        )
    .then(res => {
        this.setState({quote: [...res.data]});
    });
    }

    render(){
        return(
            <div className="container">
            <p>
                {this.state.quote}
            </p>
            </div>
        ); 
    }
}

export default QuotesComponent;
